mapper.update:
UPDATE laf_notice ln
SET ln.done          = 1,
    ln.helper_userId = #{userId}
WHERE ln.id = #{noticeId};

mapper.select:
SELECT lp.laf_photoUrl
FROM laf_notice ln
         LEFT JOIN laf_photos lp ON lp.laf_id = ln.id
WHERE ln.id = #{noticeId}
  And lp.`status` = 0;

SELECT ln.`create_id`
FROM laf_notice ln
WHERE ln.id = #{noticeId};

SELECT COUNT(*)
FROM `laf_notice` ln
         LEFT JOIN laf_photos lp ON ln.id = lp.laf_id
WHERE ln.create_id = #{userId}
  AND lp.index_display = 0;

SELECT COUNT(*)
FROM `laf_notice` ln
WHERE ln.create_id = 1540533503228669954;

SELECT COUNT(*)
FROM `laf_notice` ln
         LEFT JOIN laf_photos lp ON ln.id = lp.laf_id
WHERE ln.id = #{noticeId}
  AND ln.id = lp.laf_id;

SELECT ln.id
FROM `laf_notice` ln
WHERE ln.create_id = #{userId};
