mapper.update:
UPDATE sys_user su
SET su.PASSWORD = #{newPassword}
WHERE su.id = #{userId};

UPDATE sys_user su
SET su.user_name    = #{userName},
    su.phone_number = #{phoneNumber}
WHERE su.id = #{userId};

mapper.select:
SELECT su.id
FROM sys_user su
WHERE su.user_name = #{userName};

SELECT su.`status`
FROM `sys_user` su
WHERE su.id = #{id};

SELECT su.`password`
FROM `sys_user` su
WHERE su.id = #{userId};
